import { AllMovies } from "../types/moivesType";



const moviesReducer = (state = { movies: [], pageCount: 0 }, action) => {
    switch (action.type) {

        case AllMovies:
            return { movies: action.data }
        default:
            return state;
    }
}

export default moviesReducer
